import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  @Input () contador: number;

  constructor(private store: Store) {
  this.contador = 10;
  }

  incrementar() {
  this.contador ++;
  }

  decrementar() {
  this.contador --;
  }
}
