import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-hijo',
  templateUrl: './hijo.component.html',
  styles: []
})
export class HijoComponent {
  @Input() contador: number;
  @Output() cambioContador = new EventEmitter <number>();

  constructor() {
    this.contador = 0;
  }

  ngOnInit() {
  }

  multiplicar() {
    this.contador *= 2;
    this.cambioContador.emit(this.contador);
    }

  dividir() {
    this.contador /= 2;
    this.cambioContador.emit(this.contador);
  }

  resetNieto(nuevoContador:number){
    this.contador = nuevoContador;
    this.cambioContador.emit(this.contador);
  }

}
<h3>Contador</h3>
<h4>{{ contador }}</h4>

<div class="row">
  <div class="col">

  <button (click)= "multiplicar()" class="btn btn-primary">Multiplicar</button>
  <button (click)= "dividir()" class="btn btn-info">Dividir</button>
  </div>
</div>

<hr>

<div class="row text-center">
  <div class="col">
  <app-nieto [contador]="contador" (contadorCambio)="resetNieto($event)"></app-nieto>
  </div>
</div>
