import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-hijo',
  templateUrl: './hijo.component.html',
  styles: []
})
export class HijoComponent {
  @Input() contador: number;

  constructor() {
    this.contador = 0;
  }

  ngOnInit() {
  }

  multiplicar() {
    this.contador *= 2;
    }

  dividir() {
    this.contador /= 2;

  }

}
