import { Component, Input } from '@angular/core';
import { Store, Action } from '@ngrx/store';

interface AppState{
  contador: number;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  @Input () contador: number;

  constructor(private store: Store<AppStateany>) {
  this.contador = 10;
  this.store.subscribe(state => {
    this.contador = state.contador;
    });
  }

  incrementar() {
  //this.contador ++;
  const accion: Action = {
    type: 'INCREMENTAR'
    };
    this.store.dispatch( accion );
  }

  decrementar() {
  //this.contador --;
  const accion: Action = {
    type: 'DECREMENTAR'
    };
    this.store.dispatch( accion );
  }
}
<div class="row text-center">
  <div class="col">
    <h1>Contador</h1>
    <!--<h2>{{ contador }}</h2>-->
  </div>
</div>


<div class="row text-center">
  <div class="col">
    <button (click)="incrementar()" class="btn btn-primary">Incrementar</button>
    <button (click)="decrementar()" class="btn btn-info">Decrementar</button>
  </div>
</div>

