import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {

  @Input () contador: number;

  constructor() {
  this.contador = 10;
  }

  incrementar() {
  this.contador ++;
  }

  decrementar() {
  this.contador --;
  }
}
<div class="row text-center">
  <div class="col">
    <h1>Contador</h1>
    <h2>{{ contador }}</h2>
  </div>
</div>

