import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-hijo',
  templateUrl: './hijo.component.html',
  styles: []
})
export class HijoComponent {
  @Input() contador: number;

  constructor() {
    this.contador = 0;
  }

  ngOnInit() {
  }
}
<h3>Contador</h3>
<h4>{{ contador }}</h4>

<div class="row">
  <div class="col">

  <button class="btn btn-primary">Multiplicar</button>
  <button class="btn btn-info">Dividir</button>
  </div>
</div>
