import { Component, OnInit, Input} from '@angular/core';
import { Pokemon } from 'src/app/models/pokemon.model';
import { PokemonService } from 'src/app/services/pokemon.service';

@Component({
  selector: 'app-pokemon',
  templateUrl: './pokemon.component.html',
  styleUrls: ['./pokemon.component.css']
})
export class PokemonComponent implements OnInit{
  @Input() pokemon:any
  constructor(){

  }
  ngOnInit(): void {
    //this.pokemon = this.pokemonService.getPokemon(1).subscribe(result=> this.pokemon = result);
  }
}
<div class="container mt-5 d-flex justify-content-center">

  <div class="card p-3">

    <div class="d-flex align-items-center">

      <div class="image">
        <img src="{{pokemon.sprites.front_default}}" class="rounded" width="155">
      </div>

      <div class="ml-3 w-100">

        <h4 class="mb-0 mt-0">{{pokemon.id}}. {{pokemon.name}}</h4>
        <span>Pokemon</span>

        <div class="p-2 mt-2 bg-primary d-flex justify-content-between rounded text-white stats">

          <div class="d-flex flex-column">

            <span class="articles">Altura</span>
            <span class="number1">{{pokemon.height}}</span>

          </div>

          <div class="d-flex flex-column">

            <span class="followers">Weight</span>
            <span class="number2">{{pokemon.weight}}</span>

          </div>


          <div class="d-flex flex-column">

            <span class="rating">Base Experience</span>
            <span class="number3">{{pokemon.base_experience}}</span>

          </div>

        </div>

        <div class="button mt-2 d-flex flex-row align-items-center">
          <!-- Sprites <br>
          <button class="btn btn-sm btn-outline-primary w-100" (click)="beforeSprite()">Anterior</button>
          <button class="btn btn-sm btn-primary w-100 ml-2" (click)="nextSprite">Siguiente</button> -->

        </div>

      </div>

    </div>

  </div>

</div>
